import React from 'react';
import Slider from 'react-slick';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';
import ProductCard from './ProductCard';
import { NextArrow, PrevArrow } from './Arrows'; // Импортируйте компоненты стрелок

const ProductSlider = ({ products }) => {
  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 3,
    slidesToScroll: 1,
    nextArrow: <NextArrow />, // Добавьте кастомную стрелку "вперед"
    prevArrow: <PrevArrow />, // Добавьте кастомную стрелку "назад"
    responsive: [
      {
        breakpoint: 1024,
        settings: {
          slidesToShow: 2,
          slidesToScroll: 1,
          infinite: true,
          dots: true
        }
      },
      {
        breakpoint: 600,
        settings: {
          slidesToShow: 1,
          slidesToScroll: 1,
          initialSlide: 1
        }
      }
    ]
  };

  return (
    <Slider {...settings}>
      {products.map(product => (
        <div key={product.id} style={{ padding: '0 10px' }}>
          <ProductCard product={product} />
        </div>
      ))}
    </Slider>
  );
};

export default ProductSlider;
