import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import { Container, Row, Col, Image } from 'react-bootstrap';

const ProductPage = () => {
  const { id } = useParams();
  const [product, setProduct] = useState(null);
  const token = localStorage.getItem('token');

  useEffect(() => {
    const fetchProduct = async () => {
      try {
        const response = await axios.get(`http://localhost:3000/api/products/${id}`, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        setProduct(response.data.product); // Извлекаем объект product из ответа
      } catch (error) {
        console.error('Error fetching product:', error);
      }
    };

    fetchProduct();
  }, [id, token]);

  if (!product) {
    return <div>Loading...</div>;
  }

  return (
    <Container style={{ marginTop: '3vh' }}>
      <Row style={{display: 'flex', alignItems: 'center'}}>
        <Col md={12}>
          <div>
            <Image style={{width: '40%'}} src={`http://localhost:3000/uploads/${product.img}`} fluid />
            <div style={{border: '1px solid #000', padding: '10px', borderRadius: '20px', maxWidth: '500px', margin: '0 auto'}}>
              <h1>{product.name}</h1>
              <p>{product.description}</p>
              <p>Цена: {product.price} руб.</p>
              <p>Производитель: {product.manufacturer}</p>
              <p>Категория: {product.category.name}</p>
            </div>
          </div>
        </Col>
      </Row>
    </Container>
  );
};

export default ProductPage;
