import React from 'react';
import { Card, Button } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';
import { FaStar, FaShoppingCart } from 'react-icons/fa';

const ProductCard = ({ product }) => {
  const navigate = useNavigate();

  const handleClick = () => {
    navigate(`/product/${product.id}`);
  };

  // Пример данных для рейтинга
  const rating = 4.5; // Замените на реальные данные

  // Преобразование цены в число
  const price = parseFloat(product.price);

  // Расчет скидки
  const discountPercentage = 15;
  const discountPrice = price - (price * (discountPercentage / 100));

  // Функция для отображения звездочек рейтинга
  const renderStars = (rating) => {
    const stars = [];
    const fullStars = Math.floor(rating);
    const hasHalfStar = rating % 1 !== 0;

    for (let i = 0; i < fullStars; i++) {
      stars.push(<FaStar key={i} color="#ffd700" />);
    }

    if (hasHalfStar) {
      stars.push(<FaStar key="half" color="#ffd700" style={{ position: 'relative', clipPath: 'inset(0 0 0 50%)' }} />);
    }

    const emptyStars = 5 - stars.length;
    for (let i = 0; i < emptyStars; i++) {
      stars.push(<FaStar key={`empty-${i}`} color="#e4e7e9" />);
    }

    return stars;
  };

  return (
    <Card
      style={{
        width: '18rem',
        margin: '1rem',
        borderRadius: '10px',
        boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
        transition: 'transform 0.2s',
        position: 'relative',
      }}
      onClick={handleClick}
    >
      <Card.Img
        variant="top"
        src={`http://localhost:3000/uploads/${product.img}`}
        style={{ height: '200px', objectFit: 'cover', borderTopLeftRadius: '10px', borderTopRightRadius: '10px' }}
      />
      <Card.Body>
        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '10px' }}>
          <Card.Title style={{ fontSize: '1.2rem', fontWeight: 'bold', color: '#333', margin: 0 }}>{product.name}</Card.Title>
          <div style={{ display: 'flex', alignItems: 'center' }}>
            {renderStars(rating)}
            <span style={{ marginLeft: '5px', fontSize: '1rem', color: '#666' }}>{rating}</span>
          </div>
        </div>
        <Card.Text style={{ fontSize: '1rem', color: '#666', marginBottom: '10px' }}>{product.description}</Card.Text>
        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '10px' }}>
          <div>
            <span style={{ fontSize: '1.2rem', fontWeight: 'bold', color: '#333', textDecoration: 'line-through' }}>{price.toFixed(2)} руб.</span>
            <span style={{ fontSize: '1.4rem', fontWeight: 'bold', color: '#d32f2f', marginLeft: '10px' }}>{discountPrice.toFixed(2)} руб.</span>
          </div>
        </div>
        <Button variant="primary" style={{ width: '100%', backgroundColor: '#5a3188', borderColor: '#5a3188' }}>
          <FaShoppingCart style={{ marginRight: '5px' }} />
          В корзину
        </Button>
      </Card.Body>
    </Card>
  );
};

export default ProductCard;
