import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import { Container, Card } from 'react-bootstrap';

const ProductPage = () => {
  const { id } = useParams();
  const [product, setProduct] = useState(null);
  const token = localStorage.getItem('token');

  useEffect(() => {
    const fetchProduct = async () => {
      try {
        const response = await axios.get(`http://localhost:3000/api/products/${id}`, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        setProduct(response.data);
      } catch (error) {
        console.error('Error fetching product:', error);
      }
    };

    fetchProduct();
  }, [id]);

  if (!product) {
    return <div>Loading...</div>;
  }

  return (
    <Container>
      <Card style={{ marginTop: '2rem' }}>
        <Card.Img variant="top" src={`http://localhost:3000/uploads/${product.img}`} style={{ height: '400px', objectFit: 'cover' }} />
        <Card.Body>
          <Card.Title>{product.name}</Card.Title>
          <Card.Text>{product.description}</Card.Text>
          <Card.Text>Цена: {product.price} руб.</Card.Text>
          <Card.Text>Производитель: {product.manufacturer}</Card.Text>
          <Card.Text>Категория: {product.category}</Card.Text>
        </Card.Body>
      </Card>
    </Container>
  );
};

export default ProductPage;
