import React from 'react';
import { Card, Button } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';
import { FaStar, FaHeart, FaShoppingCart } from 'react-icons/fa';

const ProductCard = ({ product }) => {
  const navigate = useNavigate();

  const handleClick = () => {
    navigate(`/product/${product.id}`);
  };

  // Пример данных для рейтинга и скидок
  const rating = 4.5; // Замените на реальные данные
  const discountPrice = 150; // Замените на реальные данные

  return (
    <Card
      style={{
        width: '18rem',
        margin: '1rem',
        borderRadius: '10px',
        boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
        transition: 'transform 0.2s',
        position: 'relative',
      }}
      onClick={handleClick}
    >
      <div style={{ position: 'absolute', top: '10px', right: '10px', color: '#ff6b6b', cursor: 'pointer' }}>
        <FaHeart size={24} />
      </div>
      <Card.Img
        variant="top"
        src={`http://localhost:3000/uploads/${product.img}`}
        style={{ height: '200px', objectFit: 'cover', borderTopLeftRadius: '10px', borderTopRightRadius: '10px' }}
      />
      <Card.Body>
        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '10px' }}>
          <Card.Title style={{ fontSize: '1.2rem', fontWeight: 'bold', color: '#333', margin: 0 }}>{product.name}</Card.Title>
          <div style={{ display: 'flex', alignItems: 'center' }}>
            <FaStar color="#ffd700" />
            <span style={{ marginLeft: '5px', fontSize: '1rem', color: '#666' }}>{rating}</span>
          </div>
        </div>
        <Card.Text style={{ fontSize: '1rem', color: '#666', marginBottom: '10px' }}>{product.description}</Card.Text>
        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
          <div>
            <span style={{ fontSize: '1.2rem', fontWeight: 'bold', color: '#333', textDecoration: 'line-through' }}>{product.price} руб.</span>
            <span style={{ fontSize: '1.4rem', fontWeight: 'bold', color: '#d32f2f', marginLeft: '10px' }}>{discountPrice} руб.</span>
          </div>
          <Button variant="primary" style={{ backgroundColor: '#5a3188', borderColor: '#5a3188' }}>
            <FaShoppingCart style={{ marginRight: '5px' }} />
            В корзину
          </Button>
        </div>
      </Card.Body>
    </Card>
  );
};

export default ProductCard;
