import React from 'react';
import { Card, Button } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';

const ProductCard = ({ product }) => {
  const navigate = useNavigate();

  const handleClick = () => {
    navigate(`/product/${product.id}`);
  };

  return (
    <Card
      style={{
        width: '18rem',
        margin: '1rem',
        borderRadius: '10px',
        boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
        transition: 'transform 0.2s',
      }}
      onClick={handleClick}
    >
      <Card.Img
        variant="top"
        src={`http://localhost:3000/uploads/${product.img}`}
        style={{ height: '200px', objectFit: 'cover', borderTopLeftRadius: '10px', borderTopRightRadius: '10px' }}
      />
      <Card.Body>
        <Card.Title style={{ fontSize: '1.2rem', fontWeight: 'bold', color: '#333' }}>{product.name}</Card.Title>
        <Card.Text style={{ fontSize: '1rem', color: '#666' }}>{product.description}</Card.Text>
        <Card.Text style={{ fontSize: '1.2rem', fontWeight: 'bold', color: '#333' }}>Цена: {product.price} руб.</Card.Text>
        <Button variant="primary" style={{ width: '100%', marginTop: '1rem' }}>Подробнее</Button>
      </Card.Body>
    </Card>
  );
};

export default ProductCard;
